import React, { useState } from 'react';
import axios from 'axios';

const Carrito = ({ carrito, setCarrito }) => {
  const [totalPrecios, setTotalPrecios] = useState(0);
  
  const agregarAlCarrito = (producto, precio) => {
    setCarrito([...carrito, { producto: producto, precio: precio }]);
  };

  const eliminarDelCarrito = (productoAEliminar, precio) => {
    const index = carrito.findIndex(item => item.producto === productoAEliminar && item.precio === precio);
    if (index !== -1) {
      const nuevoCarrito = [...carrito.slice(0, index), ...carrito.slice(index + 1)];
      setCarrito(nuevoCarrito);
    }
  };

  const finalizarCompra = () => {
    // Lógica para finalizar la compra
    let totalPreciosTemp = 0;
    for (let i = 0; i < carrito.length; i++) {
      totalPreciosTemp += carrito[i].precio;
    }
    setTotalPrecios(totalPreciosTemp);
    setCarrito([]);
  };

  const saveCarrito = async () => {
    try {
      const response = await axios.post('http://localhost:3001/compras', {
        Elementos: carrito
      });
      return response;
    } catch (error) {
      console.error('Error al cargar la compra:', error);
    }
  };

  return (
    <div>
      <h2>Carrito de compras</h2>
      <ul>
        {carrito && carrito.length > 0 ? (
          carrito.map((item, index) => (
            <li key={index}>
              {item.producto} - {item.precio}
              <button onClick={() => eliminarDelCarrito(item.producto, item.precio)}>Eliminar</button>
            </li>
          ))
        ) : (
          <li>No hay elementos en el carrito</li>
        )}
      </ul>
      <button onClick={finalizarCompra}>Finalizar compra</button>
    </div>
  );
};

export default Carrito;
