import React from "react";

const Detalle = ({producto}) => {


    return(
        <div className='detail'>
        {productos.map(producto => (
          <div key={producto.id}>
            <h3>{producto.Name}</h3>
            <p>{producto.Descripcion}</p>
            <p>Precio: ${producto.Precio}</p>
            <p>Stock: {producto.Stock}</p>
            <img src={producto.Imagen} alt={producto.Name} style={{ width: '100px' }} />
            <button onClick={() => {
              if (producto.Name && producto.Precio) {
                props.agregarAlCarrito(producto.Name, parseInt(producto.Precio));
              }}}>Comprar</button>
              <button onClick={() => {
              if (producto.Name && producto.Precio) {
                props.eliminarDelCarrito(producto.Name, producto.Precio);
              }}}>Eliminar del carrito</button>
          </div>
        ))}
      </div>
    );
}

export default Detalle;
