import Descuentos from '../../Aplicaciones-Interactivas--Nahuel-Searchbar-y-filtrado-en-cat-logo/src/Descuentos';
import React, { useState } from 'react';

const Checkout = function(props) {
    const [mostrarDescuentos, setMostrarDescuentos] = useState(false);
    const [precioTotal, setPrecioTotal] = useState(props.totalPrecios);
   
    const actualizarPrecio = (nuevoPrecio) => {
        setPrecioTotal(nuevoPrecio);
    }

    const ElegirMetodoDePago = () => {
        setMostrarDescuentos(true);
    }
    
   
    
    return( 
        <>
            <h1>Compra Exitosa</h1>
            <h2>Precio de la compra: {precioTotal}</h2>

            {!mostrarDescuentos && <button onClick={ElegirMetodoDePago}>Elegir MÃ©todo de Pago</button>}
            { mostrarDescuentos && <Descuentos actualizarPrecio={actualizarPrecio} totalPrecios={precioTotal} mostrarDescuentos={mostrarDescuentos} setMostrarDescuentos={setMostrarDescuentos} />}
           
        </>
    );
}

export default Checkout;
