import { useState } from "react"
import { useDispatch,useSelector } from "react-redux";
import { addProduct, changeProduct } from "./Redux/ProductoSlice";
import { deleteProduct } from "./Redux/ProductoSlice";
import { almacenaProduct } from "../slices/productoSlice"
const FormularioBorrarProductos =({Producto})=>{
    const user = useSelector((state) => state.user);
    const producto = useSelector((state) => state.producto);
    const dispatch = useDispatch()
    const [message, setMessage] = useState("");
    const handleBorrar = async () => {
        try {
            console.log(producto.id)
            const response = await fetch(`http://localhost:4002/productos/borrarProducto/${Producto.id}`, {
                method: "DELETE",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${user.Token}`,
                },
            });

            const data = await response.json();
            if (response.ok) {
                dispatch(deleteProduct());
                setMessage(` ${data.message}`)
                
            } else {
                setMessage(`Error: ${data.message}`);
                
            }
        } catch (error) {
            console.error('Error al buscar el producto:', error);
            setMessage(`Error: ${error.message}`);
            
        }
    };

    return(
        <>
         <div>
                <p>¿Está seguro de que desea eliminar el producto {producto.nombre}?</p>
                <button onClick={handleBorrar}>Borrar Producto</button>
            </div>
            {message && <p>{message}</p>}
        
        </>
    )
}
export default FormularioBorrarProductos;
