import React, { useState, useEffect } from 'react';
import Navbar from './Navbar'; 
import { useNavigate } from 'react-router-dom';
import './estilos/CatalogoStyles.css';

const Catalogo = () => {
  const user = useSelector((state) => state.user);
  const [productos, setProductos] = useState([]);
  const navigate = useNavigate();

  useEffect( async () => {
    try {
      const response = await fetch(`http://localhost:4002/productos`, {
          method: "GET",
          headers: {
              "Content-Type": "application/json",
              "Authorization": `Bearer ${user.Token}`,
          },
      });

      if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log(data);

      if (data.length > 0) {
          dispatch(addProduct(data[0])); // Añade el primer producto del array (asumiendo que solo hay uno)
          dispatch(selectProduct(data[0])); // Selecciona el primer producto como producto seleccionado
          setEncontro(true);
          setError(false);
      } else {
          setError("No se encontraron productos.");
          setEncontro(false);
      }
  } catch (error) {
      console.error('Error al buscar el producto:', error);
      setError(`Error: ${error.message}`);
      setEncontro(false);
  }
});

  const abrirDetalle = (producto) => {
    navigate('/Detalle', { state: { producto } });
  };
  

  return (
    <div>
      <h1 className='titulo' >Camisetas Originals</h1> 
      <Navbar />
      <h1 className='subtitulo'>Catalogo de productos</h1>
      <div className='productos'>
        {productos.map(producto => (
          <div onClick={() => abrirDetalle(producto)} key={producto.id} className='product'>
            <h3>{producto.Name}</h3>
            <p>Precio: ${producto.Precio}</p>
            <img src={producto.Imagen} alt={producto.Name} style={{ width: '150px' }} />
            
          </div>
        ))}
      </div>
      <footer className='footer'>
  <p>© 2024 Tienda de Remeras. Todos los derechos reservados.</p>
</footer>
    </div>
  );
};

export default Catalogo;
