import React, { useState, useEffect } from "react";
import axios from "axios";
import './estilos/NavbarStyles.css'

const Navbar = ({ props }) => {
  const [productos, setProductos] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [productosEncontrados, setProductosEncontrados] = useState(true);
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    const fetchProductos = async () => {
      try {
        const response = await axios.get('http://localhost:3001/products');
        setProductos(response.data);
      } catch (error) {
        console.error('Error al obtener productos:', error);
      }
    };

    fetchProductos();
  }, []);


  // Función para filtrar los productos en función del término de búsqueda
  const filteredProductos = productos.filter(producto =>
    producto.Name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Función para filtrar los productos en función del rango de precios
  const priceFilteredProductos = filteredProductos.filter(producto =>
    (!minPrice || parseInt(producto.Precio) >= parseInt(minPrice)) &&
    (!maxPrice || parseInt(producto.Precio) <= parseInt(maxPrice))
  );

  useEffect(() => {
    setFadeIn(false); // Reiniciamos la animación al cambiar los productos filtrados
    setTimeout(() => {
      setFadeIn(true); // Establecemos la opacidad en 1 para mostrar los productos gradualmente
    }, 300); // Establecemos un retraso de 300ms para que la animación sea más visible
  }, [searchTerm, priceFilteredProductos]);

  useEffect(() => {
    if (searchTerm && priceFilteredProductos.length === 0) {
      setProductosEncontrados(false);
    } else {
      setProductosEncontrados(true);
    }
  }, [searchTerm, priceFilteredProductos]);

  return (
    <div className="navbar-container">
      {/* Barra de búsqueda */}
      <input
        type="text"
        placeholder="Buscar productos..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        className="input-search"
      />
      {/* Filtro por precio */}
      <div className="price-filters">
        <input
          type="number"
          placeholder="Precio mínimo"
          value={minPrice}
          onChange={e => setMinPrice(e.target.value)}
        />
        -
        <input
          type="number"
          placeholder="Precio máximo"
          value={maxPrice}
          onChange={e => setMaxPrice(e.target.value)}
        />
      </div>
      <div className="results">
        {!productosEncontrados && <p className="products-found">No se encontraron productos.</p>}
        {searchTerm && priceFilteredProductos.length > 0 && (
          priceFilteredProductos.map(producto => (
            <article className={`article ${fadeIn ? 'fade-in' : ''}`}>
                <div key={producto.id}>
                <h3>{producto.Name}</h3>
                <p>Precio: ${producto.Precio}</p>
                <img src={producto.Imagen} alt={producto.Name} style={{ width: '100px' }} />
                <br />
                <button onClick={() => {
                    if (producto.Name && producto.Precio) {
                    props.agregarAlCarrito(producto.Name, parseInt(producto.Precio));
                    }
                }}>Comprar</button>
                <button onClick={() => {
                    if (producto.Name && producto.Precio) {
                    props.eliminarDelCarrito(producto.Name, producto.Precio);
                    }
                }}>Eliminar del carrito</button>
                </div>
            </article>
          ))
        )}
      </div>
    </div>
  );
}

export default Navbar;
