import React, { useContext, useState,useEffect } from "react";
import   './estilos/LogStyles.css';
import { useDispatch } from "react-redux";
import { addUser } from "./Redux/UserSlice";
const Registro = () =>{
    const  dispatch = useDispatch();
    const [rol, setRol] = useState("");
    const [nombre, setNombre] = useState("");
    const [apellido, setapellido] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const handlerRegistro = (e) => {
        e.preventDefault();
        
        fetch("http://localhost:4002/api/v1/auth/register", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ email, password,nombre,apellido,rol }),
        })
            .then((response) => response.json())
            .then((data) => {
                console.log("Server response:", data);
                if (data.success) {
                    console.log("hasta aca ok")
                    dispatch(addUser({ Mail: email, Contraseña: password,Nombre:nombre,Apellido:apellido,Rol:rol }));
                } else {
                    console.log("Error registering user: ", data.message || "Unknown error");
                }
            })
            .catch((error) => console.log("Error:", error));
    };
    return(
        <><h1 className='titulo' >Camisetas Originals</h1> 
         <div className="Registrar">
            
            <h3>Crea una cuenta:</h3>
            <div className="Input">
                <label className="etiqueta">Correo Electronico:</label>
                <input
                            type="email"
                            placeholder="Ingrese su correo electronico"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
            
            </div>
            <div className="Input">
            <label className="etiqueta">Contraseña</label>
            <input
                            type="password"
                            placeholder="Ingrese su contraseña"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
            </div>
           
            <div className="Input">
            <label className="etiqueta">nombre</label>
            <input
                            type="nombre"
                            placeholder="Ingrese su nombre"
                            value={nombre}
                            onChange={(e) => setNombre(e.target.value)}
                        />
            </div>
            <div className="Input">
            <label className="etiqueta">apellido</label>
            <input
                            type="apellido"
                            placeholder="Ingrese su apellido"
                            value={apellido}
                            onChange={(e) => setapellido(e.target.value)}
                        />
            </div>
            <div className="Input">
            <label className="etiqueta">rol</label>
            <input
                            type="rol"
                            placeholder="Ingrese su rol"
                            value={rol}
                            onChange={(e) => setRol(e.target.value)}
                        />
            </div>
            <button on onClick= {handlerRegistro} className="BotonDeInicio" type="submit">Registrarse</button>
        </div>
        <footer className='footer'>
            <p>© 2024 Tienda de Remeras. Todos los derechos reservados.</p>
        </footer>
        </>
       
    )
    
}
export default Registro;
