import React, { useContext, useState } from "react";
import { Navigate, useNavigate } from "react-router-dom";
import { useDispatch,useSelector } from "react-redux";
import { loginUser } from './Redux/UserSlice';
import MensajeDeInicio from "./mensajes/MensajeDeInicio";
import MensajeDeContaInc from "./mensajes/MensajeDeContaInc";
import { changeMail,changeContra } from "./Redux/UserSlice";
const MasOpciones =()=>{
    const {email,setEmail} = useState("");
    const{password,setPassword} = useState("");
    const [newEmail, setNewEmail] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [message, setMessage] = useState("");
    const dispatch = useDispatch();
    const user = useSelector((state) => state.user);

    const handleChangeEmail = async () => {
        try {
            const response = await fetch(`http://localhost:4002/Usuario/cambiarMail`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${user.Token}`,
                },
                body: JSON.stringify({
                    mailAct: user.Mail,
                    mailNuevo: newEmail,
                }),
            });
            const data = await response.json();
            if (response.ok) {
                dispatch(changeMail(newEmail));
                setMessage("Email cambiado exitosamente.");
            } else {
                setMessage(`Error: ${data.message}`);
            }
        } catch (error) {
            console.error("Error:", error);
            setMessage("Error al cambiar el email.");
        }
    };
    const handleChangePassword = async () => {
        try {
            const response = await fetch('http://localhost:4002/Usuario/{{mail}}/{{contraActual}}/{{contraNueva}}', {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${user.Token}`,
                },
            });

            const data = await response.json();
            if (response.ok) {
                dispatch(changeContra(newPassword));
                setMessage("Contraseña cambiada exitosamente.");
            } else {
                setMessage(`Error: ${data.message}`);
            }
        } catch (error) {
            console.error("Error:", error);
            setMessage("Error al cambiar la contraseña.");
        }
    };




    return(
        <>
        <h1 className='titulo' >Camisetas Originals</h1> 
        <div className="Input">
                <label className="etiqueta">Correo Electronico:</label>
                <input
                            type="email"
                            placeholder="Ingrese su correo electronico"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
            
            </div>
            <div className="Input">
                <label className="etiqueta">Contraseña</label>
                <input
                            type="password"
                            placeholder="Ingrese su contraseña"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
            </div>
            <button type="submit" onClick={Verificar}>Verificar</button>
            <div className="Input">
                <label className="etiqueta">Correo Electronico Nuevo:</label>
                <input
                            type="email"
                            placeholder="Correo electronico nuevo"
                            value={newEmail}
                            onChange={(e) => setNewEmail(e.target.value)}
                        />
            
            <button onClick={handleChangeEmail}>Cambiar Correo</button>
            </div>
            <div className="Input">
                <label className="etiqueta">Nueva Contraseña:</label>
                <input
                    type="password"
                    placeholder="Ingrese su nueva contraseña"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                />
                <button onClick={handleChangePassword}>Cambiar Contraseña</button>
            </div>
        
            {message && <p>{message}</p>}
        
        </>
        
    )
}


export default MasOpciones;
