import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    productos: [], 
    productoSeleccionado: {
      Id: "",
      Nombre: "",
      Descripcion: "",
      Stock: "",
      Precio: "",
      Imagen: "",
      Talle: ""
    }
  };

export const productoSlice = createSlice({
    name: "producto",
    initialState,
    reducers: {
        addProduct: (state, action) => {
            state.productos.push(action.payload);
          },
          selectProduct: (state, action) => {
            state.productoSeleccionado = action.payload;
          },
        changeProduct: (state, action) => {
            const {Nombre, Descripcion, Stock, Precio, Imagen, Talle} = action.payload;
            state.Nombre = Nombre;
            state.Descripcion = Descripcion;
            state.Stock = Stock;
            state.Precio = Precio;
            state.Imagen = Imagen;
            state.Talle = Talle;
        },
        deleteProduct: (state) => {
            state.Id = "";
            state.Nombre = "";
            state.Descripcion = "";
            state.Stock = "";
            state.Precio = "";
            state.Imagen = "";
            state.Talle = "";
        },
        almacenaProduct: (state, action) => {
            const { Id, Nombre, Descripcion, Stock, Precio, Imagen, Talle } = action.payload;
            state.Id = Id;
            state.Nombre = Nombre;
            state.Descripcion = Descripcion;
            state.Stock = Stock;
            state.Precio = Precio;
            state.Imagen = Imagen;
            state.Talle = Talle;
        }
    }
    }
);
export const {addProduct, changeProduct, deleteProduct,almacenaProduct} = productoSlice.actions;
export default productoSlice.reducer;
