import React, { useContext, useState,useEffect } from "react";
import   './estilos/LogStyles.css';
import { useDispatch } from "react-redux";
import { addUser } from "./Redux/UserSlice";
const Registro = () =>{
    const  dispatch = useDispatch();
    useEffect(() =>{
        fetch("https://jsonplaceholder.typicode.com/users1")
        .then((response) => response.json())
        .then((data) => dispatch(addUser(data)))
        .catch((error) =>console.log(error));
    },[]);

    const handlerRegistro =(e)=>{
        dispatch(addUser(e.target.value))

    }
    return(
        <><h1 className='titulo' >Camisetas Originals</h1> 
         <div className="Registrar">
            
            <h3>Crea una cuenta:</h3>
            <div className="Input">
                <label className="etiqueta">Correo Electronico:</label>
                <input type="Mail"  placeholder="Mail" onSubmit={handlerRegistro} />
            
            </div>
            <div className="Input">
            <label className="etiqueta">Contraseña</label>
            <input type="password" id="password" name="password" />
            </div>
            <button className="BotonDeInicio" >Registrarse</button>
        </div>
        <footer className='footer'>
            <p>© 2024 Tienda de Remeras. Todos los derechos reservados.</p>
        </footer>
        </>
       
    )
}
export default Registro;
