import React, { useContext, useState } from 'react';
import   './estilos/LogStyles.css';
import LogIn from './LogIn';
import { useNavigate } from 'react-router-dom';
import { logoutUser } from "./Redux/UserSlice";
import MensajeDeErrorOut from './MensajeDeErrorOut';
import { useDispatch, useSelector } from "react-redux";
const OpcionesLog = () =>{
    const [mensaje,setMensaje] = useState(True);
    const  dispatch = useDispatch();
    const navigate = useNavigate();
    const user = useSelector(state => state.user);
    const handlerClick =() =>{

        navigate('/LogIn')
    }
    const handlerRegistro = () =>{
        navigate('/Registro')
    }
    const handlerLogOut =()=>{
        if (user.Mail) {
            dispatch(logoutUser());
            navigate('/LogOut');}
        else{
            setMensaje(false) && <MensajeDeErrorOut/>
        }}
    
    
    return(
    <div>
        <ul className='OpcionesLog'>
            <li className='Opcion' onClick={handlerClick}>Iniciar Sesion</li>
            <li className='Opcion' onClick={handlerLogOut}>Cerrar Sesion</li>
        </ul>
    </div>
    )

}
export default OpcionesLog;
