import React, { useContext, useState } from 'react';
import   './estilos/LogStyles.css';
import LogIn from './LogIn';
import { useNavigate } from 'react-router-dom';
import { logoutUser } from "./Redux/UserSlice";
import MensajeDeErrorOut from './mensajes/MensajeDeErrorOut';
import { useDispatch, useSelector } from "react-redux";
import MensajeDeErrorIn from './mensajes/MensajeDeErrorIn';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
const OpcionesLog = () =>{
    const [error,setError] = useState(false)
    const [mensaje,setMensaje] = useState(false);
    const  dispatch = useDispatch();
    const navigate = useNavigate();
    const user = useSelector(state => state.user);
    const HandlerVerMas=()=>{
        navigate("/MasOpciones")
    }
    const handlerClick =() =>{
        {setMensaje(false)
        if (user.Mail){
        setError(true)
    }else
        navigate('/LogIn')}
        
    }
    const handlerRegistro = () =>{
        navigate('/Registro')
    }
    const handlerLogOut =()=>{
        setError(false)
        if (user.Mail) {
            dispatch(logoutUser());
            navigate('/LogOut');}
        else{
            setMensaje(true)
            
            
        }}
    
    
    return(
    <div>
        <ul className='OpcionesLog'>
            <li className='Opcion' onClick={handlerClick}>Iniciar Sesion</li>
            <li className='Opcion' onClick={handlerLogOut}>Cerrar Sesion</li>
            <li className='Opcion'><FontAwesomeIcon icon={faPlus} title="Ver mas opciones" onClick={HandlerVerMas} />
            <span class="tooltip">Ver mas opciones</span> </li>
        </ul>
        {mensaje  && <MensajeDeErrorOut/>}
        {error && <MensajeDeErrorIn/>}
    </div>
    )

}
export default OpcionesLog;
