import { useState, useEffect } from "react"
import { useDispatch,useSelector } from "react-redux";
import { addProduct, changeProduct } from "./Redux/ProductoSlice";
import { deleteProduct } from "./Redux/ProductoSlice";
import './estilos/CatalogoStyles.css';

const HistorialDePedidos=()=>{
    const user = useSelector((state) => state.user);
    const dispatch = useDispatch()
    const [message, setMessage] = useState("");
    const [productos, setProductos] = useState([]);
    useEffect(() => {
        const handlerHistorial = async () => {
            try {
                const response = await fetch(`http://localhost:4002/Pedido/historial/${user.Mail}`, {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json",
                        "Authorization": `Bearer ${user.Token}`,
                    }
                });
    
                const data = await response.json();
    
                if (response.ok) {
                    console.log("data:");
                    console.log(data);
                    
                    // Extraer la lista de productos de cada pedido
                    const productosPedidos = data.map(pedido => pedido.productos);
                    
                    // Actualizar el estado de productos usando setProductos
                    setProductos(productosPedidos);
    
                    setMessage(`${data.message}`);
                } else {
                    setMessage(`Error: ${data.message}`);
                }
            } catch (error) {
                console.error('Error al buscar el producto:', error);
                setMessage(`Error: ${error.message}`);
            }
        };
        handlerHistorial();
    }, [user.Mail, user.Token]);
    return(
        <>
        {console.log(productos)}
        {console.log("nashe")}
        {console.log(productos[0].nombre)}
        <h1>Tus pedidos</h1>
        {productos.length > 0 ? (
                <div>
                    {productos.map((producto, index) => (
                        <div key={index}>
                            <h2>{productos[0].nombre}</h2>
                            <p>Precio: {producto.precio}</p>
                            <img src={producto.image} alt={producto.Nombre} />
                        </div>
                    ))}
                </div>
            ) : (
                <p>No hay productos en el historial de pedidos.</p>
            )}
        </>
    )
}
export default  HistorialDePedidos;
