import React from "react";
import './estilos/DetalleStyles.css'

const Detalle = ({ producto, cerrarDetalle, agregarAlCarrito, eliminarDelCarrito }) => {
    return (
        <div className='detail'>
            <button className="close-button" onClick={cerrarDetalle}>
                X
            </button>
            <div key={producto.id}>
                <h3>{producto.Name}</h3>
                <p>{producto.Descripcion}</p>
                <p>Precio: ${producto.Precio}</p>
                <p>Stock: {producto.Stock}</p>
                <img src={producto.Imagen} alt={producto.Name} style={{ width: '100px' }} />
                <br />
                <button onClick={() => {
                    if (producto.Name && producto.Precio) {
                        agregarAlCarrito(producto.Name, parseInt(producto.Precio));
                    }
                }}>Comprar</button>
                <button onClick={() => {
                    if (producto.Name && producto.Precio) {
                        eliminarDelCarrito(producto.Name, producto.Precio);
                    }
                }}>Eliminar del carrito</button>
            </div>
        </div>
    );
}

export default Detalle;
