import React, { useState, useEffect } from "react";
import './estilos/LogStyles.css';
import { useDispatch } from "react-redux";
import { addUser } from "./Redux/UserSlice";

const Registro = () => {
    const dispatch = useDispatch();

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users")
            .then((response) => response.json())
            .then((data) => dispatch(addUser({ Mail: data[0].email, Contraseña: 'defaultPassword' }))) // Ajusta según tu necesidad
            .catch((error) => console.log(error));
    }, [dispatch]);

    const handlerRegistro = (e) => {
        dispatch(addUser({ Mail: e.target.value, Contraseña: '' }));
    };

    return (
        <>
            <h1 className='titulo'>Camisetas Originals</h1>
            <div className="Registrar">
                <h3>Crea una cuenta:</h3>
                <div className="Input">
                    <label className="etiqueta">Correo Electronico:</label>
                    <input type="Mail" placeholder="Mail" onChange={handlerRegistro} />
                </div>
                <div className="Input">
                    <label className="etiqueta">Contraseña</label>
                    <input type="password" id="password" name="password" />
                </div>
                <button className="BotonDeInicio">Registrarse</button>
            </div>
            <footer className='footer'>
                <p>© 2024 Tienda de Remeras. Todos los derechos reservados.</p>
            </footer>
        </>
    );
};

export default Registro;
