import { useState } from "react"
import { useDispatch,useSelector } from "react-redux";
import { addProduct, changeProduct } from "./Redux/ProductoSlice";
import axios from "axios";
const FormularioModificarProducto = () => {
    const [id, setId] = useState("");
    const [nombre, setNombre] = useState("");
    const [descripcion, setDescripcion] = useState("");
    const [stock, setStock] = useState("");
    const [precio, setPrecio] = useState("");
    const [imagen, setImagen] = useState("");
    const [talle, setTalle] = useState("");
    const [newid, setnId] = useState("");
    const [newnombre, setnNombre] = useState("");
    const [newdescripcion, setnDescripcion] = useState("");
    const [newstock, setnStock] = useState("");
    const [newprecio, setnPrecio] = useState("");
    const [newimagen, setnImagen] = useState("");
    const [newtalle, setnTalle] = useState("");
    const user = useSelector((state) => state.user);
    const producto = useSelector((state) => state.producto);
    const dispatch = useDispatch();
    const [message, setMessage] = useState("");
    const [productoB,setProductoB]= useState("");
    const [encontro,setEncontro] = useState(false);
    const[error,setError]= useState(false)
    const buscarProducto = async () => {
        try {
            const response = await fetch(`http://localhost:4002/productos/productos/nombre/${producto.Nombre}`, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${user.Token}`,
                },
            });
            const data = await response.json();
            if (!response.ok) {
                setEncontro(true)
            }
            
        } catch (error) {
            console.error('Error al buscar el producto:', error);
            setError(`Error: ${data.message}`);
        }
    };
    const handleModificar = async () => {
        try {
            const response = await fetch("http://localhost:4002/productos/editProducto", {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${user.Token}`,
                },
                body: JSON.stringify({
                    id: newid,
                    nombre: newnombre,
                    descripcion: newdescripcion,
                    stock: newstock,
                    precio: newprecio,
                    imagen: newimagen,
                    talle: newtalle
                }),
            });
        const data = await response.json();
        if (response.ok) {
            dispatch(
                changeProduct(
                    {Id: newid, 
                        Nombre: newnombre,
                         Descripcion: newdescripcion, 
                         Stock: newstock, 
                         Precio: newprecio, 
                         Imagen: newimagen, 
                         Talle: newtalle}));
            setMessage("Producto editado exitosamente");
        } else setMessage(`Error: ${data.message}`)
        } catch (error) {
            console.log("ERROR: ", error);
            setMessage(`Error: ${data.message}`)
        }
    }

    return(
        <>
        <button onClick={buscarProducto}>Buscar Producto</button>
        {error && <p>{error}</p>}
        {encontro && <div className="addProduct">
                <label className="etiqueta">Agregue un producto</label>
                <input type="Id" placeholder="Ingrese el id" value={newid} onChange={(e) => setnId(e.target.value)} />
                <input type="Nombre" placeholder="Ingrese el nombre" value={newnombre} onChange={(e) => setnNombre(e.target.value)} />
                <input type="Descripcion" placeholder="Ingrese la descripcion" value={newdescripcion} onChange={(e) => setnDescripcion(e.target.value)} />
                <input type="Stock" placeholder="Ingrese el stock" value={newstock} onChange={(e) => setnStock(e.target.value)} />
                <input type="Precio" placeholder="Ingrese el precio" value={newprecio} onChange={(e) => setnPrecio(e.target.value)} />
                <input type="Imagen" placeholder="Ingrese la imagen" value={newimagen} onChange={(e) => setnImagen(e.target.value)} />
                <input type="Talle" placeholder="Ingrese el talle" value={newtalle} onChange={(e) => setnTalle(e.target.value)} />
                <button onClick={handleModificar} type="submit">Guardar Cambios</button>
            </div>
            }
            {message && <p>{message}</p>}
        </>
    )
}


export default FormularioModificarProducto;
