import React, { createContext, useState,useContext } from 'react';

export const CarritoContext = createContext();


export const CarritoProvider = ({ children }) => {
  const [carrito, setCarrito] = useState([]);
  
  const agregarAlCarrito = (producto, imagen,precio) => {
    setCarrito([...carrito, { producto, imagen,precio }]);
  };

  const Contador = carrito.length;

  const eliminarDelCarrito = (nombre, imagen, precio) => {
    setCarrito(carrito.filter(item => item.nombre !== producto || item.imagen !== imagen || item.precio !== precio));
    const index = carrito.findIndex(item => item.producto === nombre && item.precio === precio);
    if (index !== -1) {
      const nuevoCarrito = [...carrito];
      nuevoCarrito.splice(index, 1);
      setCarrito(nuevoCarrito);
    }
  };

  const finalizarCompra = () => {
    setCarrito([]);
  };
  
  return (
    <CarritoContext.Provider value={{ carrito, Contador,agregarAlCarrito, eliminarDelCarrito, finalizarCompra }}>
      {children}
    </CarritoContext.Provider>
  );
  
  
};export const useCarrito = () => {
  return useContext(CarritoContext);
};
